mapper.select:
update edu_list 
set edu_type=#{edu_type}, edu_name=#{edu_name}, edu_teacher=#{edu_teacher}, edu_place=#{edu_place}, 
edu_content=#{edu_content}, edu_start_date=#{edu_start_date}, edu_end_date=#{edu_end_date}, 
edu_apply_end=#{edu_apply_end}, edu_thumbnail_src=#{edu_thumbnail_src}, edu_personnel=#{edu_personnel} 
where edu_id = #{edu_id}

update his_edu 
set edu_status = '교육취소' 
where edu_his_id = #{edu_his_id}

update edu_list 
set edu_list_status = '교육확정' 
where edu_id = #{edu_id}

update his_edu 
set edu_status = '교육이수' 
where edu_his_id = #{edu_his_id}

update his_edu 
set edu_status = '교육미이수' 
where edu_his_id = #{edu_his_id}

update edu_list 
set edu_list_status = '결재중' 
where edu_id = #{edu_id}

update edu_list 
set edu_list_status = '결재완료' 
where edu_id = #{edu_id}

update edu_list 
set edu_list_status = '반려' 
where edu_id = #{edu_id}
